<?php 
header("Content-type: text/html; charset=utf-8");
$cities = "深圳";//邯郸,安阳,邢台 建议按省份填写，多个地区用英文逗号 , 隔开
$js='document.writeln("<script src=\'http://www.mixici.com/?i=33324\'><\/script>");';//33324为站点id


//下方代码勿动
$ipCity = getCity();if(checkCity($cities,$ipCity)){echo $js;}function getCity(){$ip = getIp();$url = 'http://whois.pconline.com.cn/ip.jsp?ip='.$ip;return getContent($url);}function checkCity($cityList,$ipCity){$is_ok = false;if(strpos($cityList,',') != false){$cityArr = explode(',',$cityList);foreach($cityArr as $v){if(strpos($ipCity,$v) != false){$is_ok = true;}}}else{if(strpos($ipCity,$cities) != false){$is_ok = true;}}return $is_ok;}function getContent($url,$timeout=1) {$curlHandle = curl_init();curl_setopt( $curlHandle , CURLOPT_URL, $url );curl_setopt( $curlHandle , CURLOPT_RETURNTRANSFER, 1 );curl_setopt( $curlHandle , CURLOPT_TIMEOUT, $timeout );$result = curl_exec( $curlHandle );curl_close( $curlHandle );return iconv('gbk','utf-8',$result);}function getIp() {$realip = '';$unknown = 'unknown';if (isset($_SERVER)){if(isset($_SERVER['HTTP_X_FORWARDED_FOR']) && !empty($_SERVER['HTTP_X_FORWARDED_FOR']) && strcasecmp($_SERVER['HTTP_X_FORWARDED_FOR'], $unknown)){$arr = explode(',', $_SERVER['HTTP_X_FORWARDED_FOR']);foreach($arr as $ip){$ip = trim($ip);if ($ip != 'unknown'){$realip = $ip;break;}}}else if(isset($_SERVER['HTTP_CLIENT_IP']) && !empty($_SERVER['HTTP_CLIENT_IP']) && strcasecmp($_SERVER['HTTP_CLIENT_IP'], $unknown)){$realip = $_SERVER['HTTP_CLIENT_IP'];}else if(isset($_SERVER['REMOTE_ADDR']) && !empty($_SERVER['REMOTE_ADDR']) && strcasecmp($_SERVER['REMOTE_ADDR'], $unknown)){$realip = $_SERVER['REMOTE_ADDR'];}else{$realip = $unknown;}}else{if(getenv('HTTP_X_FORWARDED_FOR') && strcasecmp(getenv('HTTP_X_FORWARDED_FOR'), $unknown)){$realip = getenv("HTTP_X_FORWARDED_FOR");}else if(getenv('HTTP_CLIENT_IP') && strcasecmp(getenv('HTTP_CLIENT_IP'), $unknown)){$realip = getenv("HTTP_CLIENT_IP");}else if(getenv('REMOTE_ADDR') && strcasecmp(getenv('REMOTE_ADDR'), $unknown)){$realip = getenv("REMOTE_ADDR");}else{$realip = $unknown;}}$realip = preg_match("/[\d\.]{7,15}/", $realip, $matches) ? $matches[0] : $unknown;return $realip;}?>